/* Now let's introduce the interactive onClick method such that the picture logo DevComp changes to the lab pic when we click on the button 
*/ 

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          greeting: 'Hello from your state, Dev Comp Psy!'
        }
    };


  handleClick(){
    this.setState({greeting: "Coucou"});
  }

  render() {
  return (
    <div className="App">
      <header className="App-header">
        <center>
        <h1>{this.state.greeting}</h1>  
        </center>
        <img src={logo} className="App-logo" alt="logo" /> 
        <a
          className="App-link"
          href="https://devcompsy.org/"
          target="_blank"
          rel="noopener noreferrer"
        >
          Find out more about us
        </a>
        <Button className="button"
          onClick = {this.handleClick}>
          Click here
        </Button>
      </header>
    </div>
  );
}

}


export default App;

class Car extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      brand: "Ford",
      model: "Mustang",
      color: "red",
      year: 1964
    };
  }
  changeColor = () => {
    this.setState({color: "blue"});
  }
  render() {
    return (
      <div>
        <h1>My {this.state.brand}</h1>
        <p>
          It is a {this.state.color}
          {this.state.model}
          from {this.state.year}.
        </p>
        <button
          type="button"
          onClick={this.changeColor}
        >Change color</button>
      </div>
    );
  }
}
