import React from 'react';               // always put it at the beginning of your component 
import logo from './logo-DevComp.png';   // import our lab image 
import App_DevComp_part2 from './App_DevComp_part2.css';           // imports the styling for your page 
import { Button } from 'react-bootstrap'; // to insert a button 

  /*
    First run an example without the onClick change just with the state 
  */

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          greeting: 'Hello from your state, Dev Comp Psy!'
        }
    };

  render() {
  return (
    <div className="App">
      <header className="App-header">
        <center>
        <h1>{this.state.greeting}</h1>  
        </center>
        <img src={logo} className="App-logo" alt="logo" /> 
        <a
          className="App-link"
          href="https://devcompsy.org/"
          target="_blank"
          rel="noopener noreferrer"
        >
          Find out more about us
        </a>
        <Button className="button">Click here</Button>
      </header>
    </div>
  );
}

}


/* We can add a child function here and pass the props to it from the parent App function 
  First, We will replace the text <h1>Hello from Dev Comp Lab!</h1>  with the Greeting function 

  function Greeting() { 
  return <h1>Hello from Dev Comp Psy!</h1>;
  }
*/ 


/* Then, we can put the props to the Greetig function from the App parent function by: 
   const greeting = 'Hello Function Component!';
   return <Headline value={greeting} />; 

and 

function Greeting(props) {
  return <h1>{props.value}</h1>;
  }
*/

export default App;
